mapper.insert:
insert into todo (date, text, `order`)
select #{todo.date}, #{todo.text}, ifnull(max(`order`), 0) + 1 from todo
where date = #{todo.date}

mapper.select:
select * from todo

    where date = #{date}

order by date desc, `order` asc

select * from todo
where date = #{date}
order by `order` asc
